%pyspark
from pyspark.sql.functions import *
from pyspark.sql.types import *

#Creating mapping for each season depending on the month value.
mapping = {'01': "Winter", '02': "Winter", '03': "Spring", '04': "Spring", '05': "Spring", '06': "Summer", '07': "Summer", '08': "Summer", '09': "Fall", '10': "Fall", '11': "Fall", '12': "Winter"}

#Creating a function that takes as input a string value for the column of a dataframe and returns the season name for the corresponding value.
def translate(mapping):
    def translate_(col):
        return mapping.get(col)
    return udf(translate_, StringType())

#Creating a new column named season, that stores the season based on the month value from the mapping create above.
dff3=dfff2.withColumn("season", translate(mapping)("Month"))

#Writing the csv file for the dff2 dataframe
#dff3.coalesce(1).write.option("header", "true").csv("Hour_Season.csv")

#Display the dataframe values.
dff3.show()
